import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPhotos } from "./Store/Store.js";
import '../src/App.css';


const App =_=> {
  const dispatch = useDispatch();
  const {photos, isLoading } = useSelector(state => state.gallery);

  useEffect(() => {
    dispatch(getPhotos());
  }, [dispatch]);

  console.log(photos);
  if (isLoading) {
    return <h1>Loading...</h1>
  }
  return (
    <div className='App'>
      <h1>Photo Gallery</h1>
      <p>This is a photo gallery made using redux toolkit and redux thunk.</p>
      <div>
        {photos.map(photo => 
        <div>
          <img key={photo.id}  
          alt={photo.author}
          src={photo.dowload_url}
          width={photo.width}
          height={photo.height}
          />
          </div>
        )}
        {/* { photos.map((photo) => {
          return (
            <div key={photo.id}>
              {photo.author}
            </div>
          )
        })} */}
      </div>
    </div>
  )
}
export default App;
